import React from 'react'

class SearchBox extends React.Component{
    state = {
        searchText: ''
    }

    handleChange = e => {
        const value = e.target.value;
        this.setState({
            searchText: value
        })
    }

    render(){
        return(
            <div className="control">
                <input class="input" type="text" placeholder="Search" value={this.state.searchText} onchange={this.handleChange}/>
                <button>Go</button>
            </div>
        );
    }
}

export default SearchBox;
