import React from 'react';
import Modal from 'react-awesome-modal';

const moment = require('moment');

class Contact extends React.Component{

    state = {
        visible: false
    }

    openModal(){
        this.setState({
            visible: true
        })
    }

    closeModal(){
        this.setState({
            visible: false
        })
    }

    render(){
        return(
            <div className="card">
                <div className="card-image">
                    <figure className="image is-4by3">
                    <img src="https://bulma.io/images/placeholders/1280x960.png" alt="" />
                    </figure>
                </div>
                <div className="card-content">
                    <div className="media">
                        <div className="media-content">
                            <p className="title is-4">{this.props.contact.name}</p>
                            <p className="subtitle is-6">@{this.props.contact.company}</p>
                        </div>
                    </div>
                    <div className="content">
                        <div>Emails:
                            <div>
                                {this.props.contact.emails.map(c => {
                                    return(
                                    <div>{c}</div>
                                    )
                                })}
                            </div>
                        </div>
                        <div>Phones:
                            <div>
                                {this.props.contact.phones.map(c => {
                                    return(
                                    <div>{c}</div>
                                    )
                                })}
                            </div>
                        </div>
                        <div>{moment(this.props.contact.last_contact).fromNow()}</div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Contact;
