import React from 'react'

var moment = require('moment');

class Meeting extends React.Component{

    state={
        addCalendar: false
    };

    getIcon = () =>{
        if(this.addCalendar){
            return(
                <i className="far fa-calendar-check fa-2x"></i>
            );
        }
        else{
            return(
                <i className="far fa-calendar-plus fa-2x"></i>
            );
        }
    };

    handleClick = () =>{
        this.setState({
            addCalendar: !addCalendar
        })
    }

    render(){
        return(
            <div className="box">
                <article className="media">
                    <div className="media-left">
                    </div>
                    <div className="media-content">
                        <div className="content">
                            <p>
                                <strong>{this.props.meeting.title}</strong><br />
                                <small>Invitees: {this.props.meeting.invitees}</small><br />
                                <small>{moment(this.props.meeting.date).fromNow()}</small>
                            </p>
                        </div>
                    </div>
                    <div className="media-right" onClick={handleClick}>{this.getIcon}</div>
                </article>
            </div>      
        );
    }
}

export default Meeting;
