import React from "react";
import Header from "./Header";
import Meetings from "./Meetings";
import Contacts from "./Contacts";
import Tweets from "./Tweets";
import Dropboxs from "./Dropboxs";
import Slacks from "./Slacks";

class Home extends React.Component {
    state = {
        search: "",
        meetings: [],
        contacts: [],
        tweets: [],
        dropboxs: [],
        slacks: [],
        allMeetings: [],
        allContacts: [],
        allTweets: [],
        allDropboxs: [],
        allSlacks: [],
    };

    componentDidMount() {
        fetch("http://localhost:3004/calendar")
            .then((response) => response.json())
            .then((data) => {
                this.setState({
                    meetings: data,
                    allMeetings: data,
                });
            });
        fetch("http://localhost:3004/contacts")
            .then((response) => response.json())
            .then((data) => {
                this.setState({
                    contacts: data,
                    allContacts: data,
                });
            });
        fetch("http://localhost:3004/tweet")
            .then((response) => response.json())
            .then((data) => {
                this.setState({
                    tweets: data,
                    allTweets: data,
                });
            });
        fetch("http://localhost:3004/dropbox")
            .then((response) => response.json())
            .then((data) => {
                this.setState({
                    dropboxs: data,
                    allDropboxs: data,
                });
            });
        fetch("http://localhost:3004/slack")
            .then((response) => response.json())
            .then((data) => {
                this.setState({
                    slacks: data,
                    allSlacks: data,
                });
            });
    }

    search = (text) => {
        text = text.toLowerCase();
        this.setState({
            search: text,
        });
        let myMeetings = [...this.state.allMeetings];
        myMeetings = myMeetings.filter((p) => {
            const index = p.matching_terms.indexOf(text);
            return index >= 0 || text === "";
        });
        let myContacts = [...this.state.allContacts];
        myContacts = myContacts.filter((p) => {
            const index = p.matching_terms.indexOf(text);
            return index >= 0 || text === "";
        });
        let myTweets = [...this.state.allTweets];
        myTweets = myTweets.filter((p) => {
            const index = p.matching_terms.indexOf(text);
            return index >= 0 || text === "";
        });
        let myDropbox = [...this.state.allDropboxs];
        myDropbox = myDropbox.filter((p) => {
            const index = p.matching_terms.indexOf(text);
            return index >= 0 || text === "";
        });
        let mySlack = [...this.state.allSlacks];
        mySlack = mySlack.filter((p) => {
            const index = p.matching_terms.indexOf(text);
            return index >= 0 || text === "";
        });
        this.setState({
            meetings: myMeetings,
            contacts: myContacts,
            tweets: myTweets,
            dropboxs: myDropbox,
            slacks: mySlack,
        });
    };

    render() {
        return (
            <div>
                <div className="fixed">
                    <Header search={this.search} />
                </div>
                <div className="columns">
                    <div className="column is-one-fifth fixed">
                        <aside class="menu">
                            <ul class="menu-list">
                                <li><a href="#meetings">Meetings</a></li>
                                <li><a href="#contacts">Contacts</a></li>
                                <li><a href="#tweets">Tweets</a></li>
                                <li><a href="#dropboxs">Dropboxs</a></li>
                                <li><a href="#slacks">Slacks</a></li>
                            </ul>
                        </aside>
                    </div>
                    <div className="column">    
                        <span name="meetings"><Meetings meetings={this.state.meetings} search={this.state.search} /></span>
                        <a name="contacts"><Contacts contacts={this.state.contacts} /></a>
                        <a name="tweets"><Tweets tweets={this.state.tweets} /></a>
                        <a name="dropboxs"><Dropboxs dropboxs={this.state.dropboxs} /></a>
                        <a name="slacks"><Slacks slacks={this.state.slacks} search={this.state.search} /></a>
                    </div>
                </div>
            </div> 
        );
    }
}

export default Home;
