import React from "react";
import Header from "./Header";
import Meetings from "./Meetings";
import Contacts from "./Contacts";
import Tweets from "./Tweets";
import Dropboxs from "./Dropboxs";
import Slacks from "./Slacks";

class Home extends React.Component {
    state = {
        search: "",
        meetings: [],
        contacts: [],
        tweets: [],
        dropboxs: [],
        slacks: [],
        allMeetings: [],
        allContacts: [],
        allTweets: [],
        allDropboxs: [],
        allSlacks: [],
    };

    componentDidMount() {
        fetch("http://localhost:3004/calendar")
            .then((response) => response.json())
            .then((data) => {
                this.setState({
                    meetings: data,
                    allMeetings: data,
                });
            });
        fetch("http://localhost:3004/contacts")
            .then((response) => response.json())
            .then((data) => {
                this.setState({
                    contacts: data,
                    allContacts: data,
                });
            });
        fetch("http://localhost:3004/tweet")
            .then((response) => response.json())
            .then((data) => {
                this.setState({
                    tweets: data,
                    allTweets: data,
                });
            });
        fetch("http://localhost:3004/dropbox")
            .then((response) => response.json())
            .then((data) => {
                this.setState({
                    dropboxs: data,
                    allDropboxs: data,
                });
            });
        fetch("http://localhost:3004/slack")
            .then((response) => response.json())
            .then((data) => {
                this.setState({
                    slacks: data,
                    allSlacks: data,
                });
            });
    }

    search = (text) => {
        text = text.toLowerCase();
        this.setState({
            search: text,
        });
        let myMeetings = [...this.state.allMeetings];
        myMeetings = myMeetings.filter((p) => {
            const index = p.matching_terms.indexOf(text);
            return index >= 0 || text === "";
        });
        this.setState({
            meetings: myMeetings,
        });
        let myContacts = [...this.state.allContacts];
        myContacts = myContacts.filter((p) => {
            const index = p.matching_terms.indexOf(text);
            return index >= 0 || text === "";
        });
        this.setState({
            contacts: myContacts,
        });
        let myTweets = [...this.state.allTweets];
        myTweets = myTweets.filter((p) => {
            const index = p.matching_terms.indexOf(text);
            return index >= 0 || text === "";
        });
        this.setState({
            tweets: myTweets,
        });
        let myDropbox = [...this.state.allDropboxs];
        myDropbox = myDropbox.filter((p) => {
            const index = p.matching_terms.indexOf(text);
            return index >= 0 || text === "";
        });
        this.setState({
            dropboxs: myDropbox,
        });
        let mySlack = [...this.state.allSlacks];
        mySlack = mySlack.filter((p) => {
            const index = p.matching_terms.indexOf(text);
            return index >= 0 || text === "";
        });
        this.setState({
            slacks: mySlack,
        });
    };

    render() {
        return (
            <div>
                <Header search={this.search} />
                <Meetings meetings={this.state.meetings} search={this.state.search} />
                <Contacts contacts={this.state.contacts} />
                <Tweets tweets={this.state.tweets} />
                <Dropboxs dropboxs={this.state.dropboxs} />
                <Slacks slacks={this.state.slacks} search={this.state.search} />
            </div>
        );
    }
}

export default Home;
